import groovy.util.*;

// Define target steps and path
def step = "AddNewContact";
def emailPath = "//*:email";

// Create object of GroovyUtils class in order to get access to the target TestStep
def groovyUtils = new com.eviware.soapui.support.GroovyUtils(context);
def request = groovyUtils.getXmlHolder("$step#Request");
def response = groovyUtils.getXmlHolder("$step#Response");

log.info("Expected Email: " + request.getNodeValues(emailPath).toString() + " | Actual Email: " + response.getNodeValues(emailPath).toString());

assert request.getNodeValues(emailPath) == response.getNodeValues(emailPath) : "Value of Email is invalid"

