import groovy.util.*;

// Define target steps and path
def step = "ViewContactByName";
def namePath = "//*:name";

// Create object of GroovyUtils class in order to get access to the target TestStep
def groovyUtils = new com.eviware.soapui.support.GroovyUtils(context);
def request = groovyUtils.getXmlHolder("$step#Request");
def response = groovyUtils.getXmlHolder("$step#Response");

//log.info request.getNodeValues(namePath)[0].toString()
//log.info response.getNodeValues(namePath)[0].toString()
//log.info response.getNodeValues(namePath)[1].toString()
//log.info response.getNodeValues(namePath).size()

int responseSize = response.getNodeValues(namePath).size();
def expectedNameValue = request.getNodeValues(namePath)[0].toString()
for(int i=0; i<responseSize; i++) {
	def actualNameValue = response.getNodeValues(namePath)[i].toString();
	log.info actualNameValue
	assert actualNameValue.contains(expectedNameValue): "Name '$actualNameValue' should contain text '$expectedNameValue'"
}
